<template>
    <div v-show="isShow" :class="['w-alert', typeClass, {
      'is-center': alignCenter,
      'is-bold': boldTitle && description
    }]" role="alert">
        <!-- icon -->
        <i v-if="showIcon" :class="['w-alert__icon', iconClass]"></i>
        <!-- content -->
        <div class="w-alert__content">
            <span v-if="title || $slots.title"
                  :class="['w-alert__title', {
                    'is-bold': boldTitle
                  }]">
                <slot name="title">{{title}}</slot>
            </span>
            <slot>
                <p v-if="description" v-text="description"
                   class="w-alert__description"></p>
            </slot>
        </div>
        <!-- close icon -->
        <i v-show="closable" v-text="closeText"
          :class="['w-alert__closebutton', {'is-text': closeText !== '', 'w-icon-close': closeText === ''}]"
          @click="handleClose"></i>
    </div>
</template>
<script>
export default {
  name: 'WAlert',
  data () {
    return {
      isShow: true
    }
  },
  props: {
    showIcon: Boolean,
    alignCenter: Boolean,
    type: {
      type: String,
      default: 'info'
    },
    title: {
      type: String,
      default: ''
    },
    description: {
      type: String,
      default: ''
    },
    closable: {
      type: Boolean,
      default: true
    },
    boldTitle: {
      type: Boolean,
      default: false
    },
    closeText: {
      type: String,
      default: ''
    }
  },
  computed: {
    typeClass () {
      return `w-alert--${this.type}`
    },
    iconClass () {
      const iconMap = {
        success: 'success',
        warning: 'warning',
        error: 'error',
        info: 'info-circle'
      }
      return `w-icon-${[iconMap[this.type]]}`
    }
  },
  methods: {
    handleClose () {
      this.isShow = false
      this.$emit('close')
    }
  }
}
</script>
