<template>
  <div :class="['w-drawer', drawerClass, `w-drawer--${position}`, {
    'is-open': showDrawer
  }]">
    <transition name="fade-in-linear">
      <div v-show="mask && showDrawer" class="w-drawer__mask"
        :style="{zIndex: zIndex}" @click="handleClose">
      </div>
    </transition>
    <transition :name="`slide-${position}`">
      <div v-show="showDrawer" :class="['w-drawer__content', {
        'is-hidden': !showDrawer
      }]" :style="{zIndex: zIndex + 1}">
        <slot></slot>
      </div>
    </transition>
  </div>
</template>

<script>
export default {
  name: 'WDrawer',
  data () {
    return {
      showDrawer: false
    }
  },
  props: {
    drawerClass: String,
    mask: {
      type: Boolean,
      default: true
    },
    maskClosable: {
      type: Boolean,
      default: true
    },
    position: {
      type: String,
      default: 'left'
    },
    visible: Boolean,
    zIndex: {
      type: Number,
      default: 9998
    }
  },
  methods: {
    handleClose () {
      if (!this.maskClosable) return
      this.showDrawer = false
    }
  },
  watch: {
    visible (val) {
      this.showDrawer = val
    },
    showDrawer (val) {
      this.$emit(val ? 'onOpen' : 'onClose')
    }
  }
}
</script>
