<template>
  <div :class="['w-dropdown-menu-item', {
    'is-divided': divided,
    'is-disabled': disabled
  }]" @click="handleClick">
    <slot></slot>
  </div>
</template>
<script>
import Emitter from '../../../mixins/emitter'
export default {
  name: 'WDropdownMenuItem',
  data () {
    return {
    }
  },
  props: {
    divided: Boolean,
    disabled: Boolean
  },
  computed: {
  },
  mixins: [Emitter],
  methods: {
    handleClick (e) {
      if (this.disabled) return
      this.dispatchEvent('WDropdownMenu', 'dropdown.menu-item-click', e)
      this.dispatchEvent('WDropdown', 'dropdown-item-click', e)
    }
  }
}
</script>
