<template>
  <transition name="w-message-fade" @after-leave="handleAfterLeave">
    <div class="w-message"
         v-show="visible"
         :style="positionStyle"
         @mouseenter="clearTimer"
         @mouseleave="startTimer"
         :class="[
          type && !iconClass ? `w-message--${ type }` : '',
          center ? 'is-center' : '',
          showClose ? 'is-closable' : '',
          customClass
          ]">
      <i :class="iconClass" v-if="iconClass"></i>
      <i class="w-message__leftIcon"
         :class="typeClass" v-else></i>
      <slot>
        <p v-if="!dangerouslyUseHTMLString" class="w-message__content">{{ message }}</p>
        <p v-else v-html="message" class="w-message__content"></p>
      </slot>
      <i v-if="showClose"
         class="w-message__closeBtn w-icon-close" @click="close"></i>
    </div>
  </transition>
</template>

<script type="text/babel">
const typeMap = {
  success: 'success',
  info: 'info-circle',
  warning: 'warning',
  error: 'error',
  loading: 'loading'
}
export default {
  name: 'WMessage',
  data () {
    return {
      visible: false,
      message: '',
      duration: 3000,
      type: 'info',
      iconClass: '',
      customClass: '',
      onClose: null,
      showClose: false,
      closed: false,
      timer: null,
      dangerouslyUseHTMLString: false,
      verticalOffset: 20,
      center: false
    }
  },
  watch: {
    closed () {
      this.visible = false
    }
  },
  computed: {
    typeClass () {
      return this.type && !this.iconClass
        ? `w-icon w-icon-${typeMap[this.type]} w-message__${typeMap[this.type]}`
        : ''
    },
    positionStyle () {
      return {
        'top': `${this.verticalOffset}px`
      }
    }
  },
  methods: {
    handleAfterLeave () {
      this.$destroy(true)
      this.$el.parentNode.removeChild(this.$el)
    },
    clearTimer () {
      clearTimeout(this.timer)
    },
    close () {
      this.closed = true
      this.onClose(this)
    },
    startTimer () {
      if (this.duration > 0) {
        this.timer = setTimeout(() => {
          if (!this.closed) {
            this.close()
          }
        }, this.duration)
      }
    },
    keydown (e) {
      if (e.keyCode && e.keyCode === 27) { // esc关闭消息
        if (!this.closed) {
          this.close()
        } else {
          return false
        }
      } else {
        return false
      }
    }
  },
  mounted () {
    this.startTimer()
    document.addEventListener('keydown', this.keydown)
  },
  beforeDestroy () {
    document.removeEventListener('keydown', this.keydown)
  }
}
</script>
