<template>
  <div
    class="w-select-dropdown w-popper"
    :class="[{ 'is-multiple': $parent.multiple }, popperClass]"
    :style="{ minWidth: minWidth }">
    <slot></slot>
  </div>
</template>

<script type="text/babel">
import Popper from '../../../mixins/popper'

export default {
  name: 'WSelectDropdown',

  componentName: 'WSelectDropdown',

  mixins: [Popper],

  props: {
    placement: {
      default: 'bottom-start'
    },

    boundariesPadding: {
      default: 0
    },

    popperOptions: {
      default () {
        return {
          gpuAcceleration: false
        }
      }
    },

    visibleArrow: {
      default: true
    },

    appendToBody: {
      type: Boolean,
      default: true
    }
  },

  data () {
    return {
      minWidth: ''
    }
  },

  computed: {
    popperClass () {
      return this.$parent.popperClass
    }
  },

  watch: {
    '$parent.inputWidth' () {
      this.minWidth = this.$parent.$el.getBoundingClientRect().width + 'px'
    }
  },
  methods: {
    getReferenceElementInParent ($parent = null) {
      $parent = $parent || this.$parent

      return $parent.$refs.hasOwnProperty('reference')
        ? $parent.$refs.reference.$el
        : this.getReferenceElementInParent($parent.$parent)
    }
  },
  mounted () {
    this.$nextTick(() => {
      // this.referenceElement = this.$parent.$refs.reference.$el
      this.referenceElement = this.getReferenceElementInParent()
      this.$parent.popperElm = this.popperElm = this.$el
      this.$on('updatePopper', () => {
        if (this.$parent.visible) {
          this.updatePopper()
        }
      })
      this.$on('destroyPopper', this.destroyPopper)
    })
  }
}
</script>
