<template>
  <div
    class="w-step"
    :style="style"
    :class="[
      `is-${$parent.direction}`,
      isLast && !space && !isCenter && 'is-flex',
      isCenter && !isVertical && 'is-center'
     ]">
    <!-- icon & line -->
    <div
      class="w-step__head" :class="`is-${currentStatus}`">
      <div
        class="w-step__line"
        :style="isLast ? '' : { marginRight: $parent.stepOffset + 'px' }"
      >
        <i class="w-step__line-inner" :class="`is-${currentStatus}`" :style="lineStyle"></i>
      </div>

      <div class="w-step__icon" :class="`is-${icon ? 'icon' : 'text'} is-${currentStatus}`">
        <slot
          v-if="currentStatus !== 'success' && currentStatus !== 'error'"
          name="icon">
          <i v-if="icon" class="w-step__icon-inner" :class="[icon]"></i>
          <div class="w-step__icon-inner" v-if="!icon">{{ index + 1 }}</div>
        </slot>
        <i
          v-else
          :class="['w-icon-' + (currentStatus === 'success' ? 'right' : 'close')]"
          class="w-step__icon-inner is-status"
        >
        </i>
      </div>
    </div>
    <!-- title & description -->
    <div class="w-step__main">
      <div
        class="w-step__title"
        ref="title"
        :class="['is-' + currentStatus]">
        <slot name="title">{{ title }}</slot>
      </div>
      <div
        class="w-step__description"
        :class="['is-' + currentStatus]">
        <slot name="description">{{ description }}</slot>
      </div>
    </div>
  </div>
</template>

<script>
export default {
  name: 'WStep',
  props: {
    title: String,
    icon: String,
    description: String,
    status: String,
  },
  data() {
    return {
      index: -1,
      lineStyle: {},
      internalStatus: ''
    };
  },
  beforeCreate() {
    this.$parent.steps.push(this);
  },
  beforeDestroy() {
    const steps = this.$parent.steps;
    const index = steps.indexOf(this);
    index !== -1 && steps.splice(index, 1)
  },
  computed: {
    currentStatus() {
      return this.status || this.internalStatus;
    },
    isCenter() {
      return this.$parent.alignCenter;
    },
    isVertical() {
      return this.$parent.direction === 'vertical';
    },
    isLast() {
      const parent = this.$parent;
      return parent.steps[parent.steps.length - 1] === this;
    },
    stepsCount() {
      return this.$parent.steps.length;
    },
    isLead() {
      return this.$parent.lead;
    },
    space() {
      const { $parent: { space } } = this;
      return space ;
    },
    style: function() {
      const style = {};
      const parent = this.$parent;
      const len = parent.steps.length;
      const space = (typeof this.space === 'number'
        ? this.space + 'px'
        : this.space
          ? this.space
          : 100 / (len - (this.isCenter ? 0 : 1)) + '%');
      style.flexBasis = space;
      if (this.isVertical) return style;
      if (this.isLast) {
        style.maxWidth = 100 / this.stepsCount + '%';
      } else {
        style.marginRight = -this.$parent.stepOffset + 'px';
      }
      return style;
    }
  },
  methods: {
    updateStatus(val) {
      const prevChild = this.$parent.$children[this.index - 1];
      if (val > this.index) {
        this.internalStatus = this.$parent.finishStatus;
      } else if (val === this.index) {
        this.internalStatus = val === this.$parent.steps.length - 1 ? this.$parent.finishStatus : this.$parent.processStatus;
      } else if (val === this.index - 1) {
        this.internalStatus = 'next';
      } else {
        this.internalStatus = 'wait';
      }
      if (prevChild) prevChild.calcProgress(this.internalStatus);
    },
    calcProgress(status) {
      let step = 100;
      const style = {};
      style.transitionDelay = 150 * this.index + 'ms';
      if (status === this.$parent.processStatus) {
        step = this.currentStatus !== 'error' ? 100 : 100;
      } else if (status === 'wait') {
        step = 0;
        style.transitionDelay = (-150 * this.index) + 'ms';
      } else if (status === 'next') {
        step = this.isLead ? 60 : 0;
      }
      style.borderWidth = step ? '1px' : 0;
      this.$parent.direction === 'vertical'
        ? style.height = step + '%'
        : style.width = step + '%';
      this.lineStyle = style;
    }
  },
  mounted() {
    const unwatch = this.$watch('index', val => {
      this.$watch('$parent.active', this.updateStatus, { immediate: true });
      this.$watch('$parent.processStatus', () => {
        const activeIndex = this.$parent.active;
        this.updateStatus(activeIndex);
      }, { immediate: true });
      unwatch();
    });
  }
};
</script>
</script>