<template>
  <div class="w-switch"
       :class="{ 'is-disabled': switchDisabled, 'is-checked': checked }"
        @click="switchValue"
  >
    <input
    type="checkbox"
    class="w-switch__input"
    ref="input">
    <!--左label-->
    <span
      :class="['w-switch__label', 'w-switch__label--left', !checked ? 'is-active' : '']"
      v-if="inactiveText">
      <span v-if="inactiveText">{{ inactiveText }}</span>
    </span>
    <span
    class="w-switch__core"
    ref="core"
    :style="{ 'width': width + 'px' }">
    </span>
    <!--右label-->
    <span
      :class="['w-switch__label', 'w-switch__label--right', checked ? 'is-active' : '']"
      v-if="activeText">
      <span v-if="activeText">{{ activeText }}</span>
    </span>
  </div>
</template>

<script>
import Emitter from '../../../mixins/emitter'
export default {
  name: 'WSwitch',
  model: {
    event: 'change'
  },
  inject: {
    wForm: {
      default: ''
    },
    wFormItem: {
      default: ''
    }
  },
  props: {
    disabled: {
      type: Boolean,
      default: false
    },
    value: {
      type: [Boolean, String, Number],
      default: false
    },
    activeValue: {
      type: [Boolean, String, Number],
      default: true
    },
    inactiveValue: {
      type: [Boolean, String, Number],
      default: false
    },
    activeText: String,
    inactiveText: String,
    width: {
      type: Number,
      default: 40
    },
    activeColor: {
      type: String,
      default: ''
    },
    inactiveColor: {
      type: String,
      default: ''
    }
  },
  watch: {
    checked () {
      this.$refs.input.checked = this.checked
      if (this.activeColor || this.inactiveColor) {
        this.setBackgroundColor()
      }
    }
  },
  computed: {
    checked () {
      return this.value === this.activeValue
    },
    switchDisabled () {
      return this.disabled
    }
  },
  mixins: [Emitter],
  methods: {
    switchValue () {
      !this.switchDisabled && this.handleChange()
    },
    handleChange () {
      const val = this.checked ? this.inactiveValue : this.activeValue
      this.$emit('change', val)
      this.dispatchEvent('WFormItem', 'w.form.field.change')
      this.$refs.input.checked = this.checked
    },
    setBackgroundColor () {
      let newColor = this.checked ? this.activeColor : this.inactiveColor
      this.$refs.core.style.borderColor = newColor
      this.$refs.core.style.backgroundColor = newColor
    }
  },
  mounted () {
    if (this.activeColor || this.inactiveColor) {
      this.setBackgroundColor()
    }
    this.$refs.input.checked = this.checked
  }
}
</script>

