<template>
  <transition name="fade-in-linear">
    <span :class="['w-tag', `w-tag--${type}`, `w-tag--${size}`]">
      <slot></slot>
      <i v-if="closable" class="w-icon-close" @click.stop="hiddenTag"></i>
    </span>
  </transition>
</template>

<script>
  export default{
    name: 'WTag',
    data () {
      return {
      }
    },
    props: {
      closable: {
        type: Boolean,
        default: false
      },
      type: {
        type: String,
        default: 'primary'
      },
      size: {
        type: String,
        default: 'medium'
      }
    },
    methods: {
      hiddenTag () {
        this.$emit('close')
      }
    }
  }
</script>
