<template>
  <div class="w-timeline">
    <slot></slot>
  </div>
</template>

<script>
export default {
  name: 'WTimeline',
  data () {
    return {
      actived: ''
    }
  },
  props: {
    position: {
      type: String,
      default: 'left'
    },
    timePosition: {
      type: String,
      default: 'bottom'
    }
  },
  mounted () {
    this.$on('node-click', (val) => {
      this.actived = val
      this.$emit('node-actived', val)
    })
  }
}
</script>
